(function($){$.shortCSS=function(o){if(o!=undefined){$.each(o,function(k,v){if(k.indexOf("*")>=0){$('*').each(function(){var t=$(this).attr('class');if(t!==undefined){rx=new RegExp(k,'ig');if(rx.test(t)){x=k.replace(/\*/g,'');y=v.replace(/\*/g,'');n=t.replace(new RegExp(x,'g'),y);$('.'+t).addClass(n).removeClass(t);}}});}else{p=v.split(' ');$.each(p,function(c,t){$('.'+k).addClass(t);console.log(k);});$('.'+k).removeClass(k);}});}};})(jQuery);
